package com.ufgov.zc.client.zc.ztb.util;import javax.swing.filechooser.FileFilter;import java.io.File;public class FileChooseFilter extends FileFilter {  private String ext;  public FileChooseFilter() {    this(null);  }  public FileChooseFilter(String ext) {    this.ext = ext;  }  @Override  public boolean accept(File f) {    if (f.isDirectory()) {      return true;    }    if (this.ext == null) {      return true;    }    String locExt = "." + this.ext.toLowerCase();    return f.getAbsolutePath().toLowerCase().endsWith(locExt);  }  @Override  public String getDescription() {    String locExt = this.ext.toLowerCase();    if (locExt.endsWith("xml")) {      return GV.getSimpleMsg("xmlDescription");    } else if (locExt.equals("doc")) {      return "Microsoft Word文件(*.doc)";    } else if (locExt.equals("docx")) {      return "Microsoft Word 2007文件(*.docx)";    } else if (locExt.equals("xls")) {      return "Microsoft Excel文件(*.xls)";    } else if (locExt.equals("xlsx")) {      return "Microsoft Excel 2007文件(*.xlsx)";    } else if (locExt.equals("ztb")) {      return "用友政务招投标文件(*.ztb)";    } else if (locExt.equals(GV.TEMPLATE_ZIP)) {      return "用友政务招标书模板文件(*.tpl)";    }    return null;  }}